mapper.select:
SELECT * FROM product WHERE prod_idx = (
    SELECT prod_idx FROM cart WHERE cart_idx=${cart_idx} AND payed='false'
)

SELECT
		(SELECT prod_price - prod_sale
		 FROM product
		 WHERE prod_idx = (
			 SELECT prod_idx
			 FROM cart
			 WHERE cart_idx = #{cart_idx}
			   AND payed = 'false'
		 )
		)
		*
		(SELECT prod_count
		 FROM cart
		 WHERE cart_idx = #{cart_idx}
		) AS total_price
